CREATE TABLE tb_NhanVien
(
    MaNV INT IDENTITY,
    HoTen NVARCHAR(60) NOT NULL,
    GioiTinh NVARCHAR(3) NOT NULL,
    SDT VARCHAR(10) NOT NULL,
