CREATE TABLE tb_DanhMuc
(
    MaDM INT IDENTITY,
    TenDM NVARCHAR(60) NOT NULL UNIQUE,
	DaXoa BIT DEFAULT 0 --0: Chưa xóa; 1: Đã xóa
    CONSTRAINT pk_DanhMuc PRIMARY KEY(MaDM)
)
GO

CREATE TABLE tb_NhomSanPham
(
    MaNSP INT IDENTITY,
    TenNSP NVARCHAR(60) NOT NULL UNIQUE,
    MaDM INT NOT NULL,
	DaXoa BIT DEFAULT 0 --0: Chưa xóa; 1: Đã xóa
    CONSTRAINT pk_NhomSP PRIMARY KEY(MaNSP),
    CONSTRAINT fk_NhomSP_DM FOREIGN KEY(MaDM) REFERENCES tb_DanhMuc(MaDM)
)
GO

CREATE TABLE tb_TrangThaiSanPham
(
	MaTTSP INT IDENTITY, --0: Ngừng kinh doanh, 1: Còn hàng, 2: Hết hàng
	TenTTSP NVARCHAR(60) NOT NULL UNIQUE,
	CONSTRAINT pk_TrangThaiSanPham PRIMARY KEY(MaTTSP)
)

CREATE TABLE tb_SanPham
(
    ID INT IDENTITY,
    TenSP NVARCHAR(120) NOT NULL UNIQUE,
    MaNSP INT NOT NULL,
	GiaNiemYet MONEY,
	SoLuongTon INT DEFAULT 0,
	ThoiGianBH INT NOT NULL, -- Tính theo tháng
	UrlHinh VARCHAR(1000),
	MoTa NVARCHAR(MAX),
	TrangThai INT DEFAULT 1,
	DaXoa BIT DEFAULT 0 --0: Chưa xóa; 1: Đã xóa
    CONSTRAINT pk_SanPham PRIMARY KEY(ID),
    CONSTRAINT fk_SanPham_NhomSP FOREIGN KEY(MaNSP) REFERENCES tb_NhomSanPham(MaNSP),
	CONSTRAINT fk_SanPham_TTSP FOREIGN KEY(TrangThai) REFERENCES tb_TrangThaiSanPham(MaTTSP)
)
GO

CREATE TABLE tb_GiaBanSP
(
	MaSP INT NOT NULL,
	NgayCapNhat DATETIME DEFAULT GETDATE(),
	GiaBan MONEY NOT NULL,
	CONSTRAINT pk_GiaBanSP PRIMARY KEY(MaSP, NgayCapNhat),
	CONSTRAINT fk_GiaBanSP_SP FOREIGN KEY(MaSP) REFERENCES tb_SanPham(ID)
)
GO

CREATE TABLE tb_HDNhapHang
(
	MaHDNH INT IDENTITY,
	MaNV INT NOT NULL,
	NgayLap DATETIME NOT NULL DEFAULT GETDATE(),
	CONSTRAINT pk_HDNhapHang PRIMARY KEY(MaHDNH),
	CONSTRAINT fk_HDNhapHang_NV FOREIGN KEY(MaNV) REFERENCES tb_NhanVien(MaNV)
)
GO

CREATE TABLE tb_CTHDNhapHang
(
	ID INT IDENTITY,
	MaHDNH INT NOT NULL,
	MaSP INT NOT NULL,
	GiaNhap MONEY NOT NULL,
	SoLuong INT DEFAULT 0,
	CONSTRAINT pk_CTHDNhapHang PRIMARY KEY(ID),
	CONSTRAINT fk_CTHDNhapHang_HDNH FOREIGN KEY(MaHDNH) REFERENCES tb_HDNhapHang(MaHDNH),
	CONSTRAINT fk_CTHDNhapHang_SP FOREIGN KEY(MaSP) REFERENCES tb_SanPham(ID)
)

CREATE TABLE tb_KhoHang
(
