CREATE OR ALTER TRIGGER trg_UpdateSluongTon
ON tb_KhoHang
AFTER INSERT, UPDATE, DELETE
AS
	DECLARE @MaCTHDNhap INT
	DECLARE @MaSP INT
	IF EXISTS (SELECT 0 FROM inserted) --insert or update
	BEGIN
		DECLARE cur_UpdateSluongTon CURSOR
		FOR SELECT MaCTHDNhap from inserted
		OPEN cur_UpdateSluongTon

		FETCH NEXT FROM cur_UpdateSluongTon INTO @MaCTHDNhap
		WHILE @@FETCH_STATUS = 0
		BEGIN
			SET @MaSP = (SELECT MaSP FROM tb_CTHDNhapHang WHERE ID = @MaCTHDNhap)
			UPDATE tb_SanPham SET SoLuongTon = (SELECT COUNT(*) FROM tb_KhoHang WHERE MaCTHDNhap IN (SELECT ID FROM tb_CTHDNhapHang WHERE MaSP=@MaSP) AND TrangThai = 0)
			WHERE ID = @MaSP
			FETCH NEXT FROM cur_UpdateSluongTon INTO @MaCTHDNhap
		END

		CLOSE cur_UpdateSluongTon
		DEALLOCATE cur_UpdateSluongTon
	END
	ELSE IF EXISTS (SELECT 0 FROM deleted)
	BEGIN
		DECLARE cur_UpdateSluongTon CURSOR
		FOR SELECT MaCTHDNhap from deleted
		OPEN cur_UpdateSluongTon

		FETCH NEXT FROM cur_UpdateSluongTon
			  INTO @MaCTHDNhap
		WHILE @@FETCH_STATUS = 0
		BEGIN
			SET @MaSP = (SELECT MaSP FROM tb_CTHDNhapHang WHERE ID = @MaCTHDNhap)
			UPDATE tb_SanPham SET SoLuongTon = (SELECT COUNT(*) FROM tb_KhoHang WHERE MaCTHDNhap IN (SELECT ID FROM tb_CTHDNhapHang WHERE MaSP=@MaSP) AND TrangThai = 0)
			WHERE ID = @MaSP
			FETCH NEXT FROM cur_UpdateSluongTon INTO @MaCTHDNhap
		END

		CLOSE cur_UpdateSluongTon
		DEALLOCATE cur_UpdateSluongTon
	END
GO

