CREATE OR ALTER PROC up_GetCTSanPham
AS
	SELECT ID, TenSP, TenNSP, GiaNiemYet, GiaBan, NgayCapNhat, SoLuongTon, ThoiGianBH, UrlHinh, TenTTSP FROM tb_SanPham SP, tb_TrangThaiSanPham TT, tb_GiaBanSP GB, tb_NhomSanPham NSP
	WHERE SP.TrangThai = TT.MaTTSP AND SP.ID = GB.MaSP AND SP.MaNSP = NSP.MaNSP
	AND SP.DaXoa = 0
	AND NgayCapNhat >= ALL (SELECT NgayCapNhat FROM tb_GiaBanSP WHERE MaSP = SP.ID)
GO

CREATE OR ALTER PROC up_GetCTSanPhamByDanhMuc @MaDM INT
AS
	SELECT ID, TenSP, TenNSP, GiaNiemYet, GiaBan, NgayCapNhat, SoLuongTon, ThoiGianBH, UrlHinh, TenTTSP FROM tb_SanPham SP, tb_TrangThaiSanPham TT, tb_GiaBanSP GB, tb_NhomSanPham NSP
	WHERE SP.TrangThai = TT.MaTTSP AND SP.ID = GB.MaSP AND SP.MaNSP = NSP.MaNSP
	AND MaDM = @MaDM AND SP.DaXoa = 0
	AND NgayCapNhat >= ALL (SELECT NgayCapNhat FROM tb_GiaBanSP WHERE MaSP = SP.ID)
GO

CREATE OR ALTER PROC up_GetCTSanPhamByNhomSP @MaNSP INT
AS
	SELECT ID, TenSP, TenNSP, GiaNiemYet, GiaBan, NgayCapNhat, SoLuongTon, ThoiGianBH, UrlHinh, TenTTSP FROM tb_SanPham SP, tb_TrangThaiSanPham TT, tb_GiaBanSP GB, tb_NhomSanPham NSP
	WHERE SP.TrangThai = TT.MaTTSP AND SP.ID = GB.MaSP AND SP.MaNSP = NSP.MaNSP
	AND NSP.MaNSP = @MaNSP AND SP.DaXoa = 0
	AND NgayCapNhat >= ALL (SELECT NgayCapNhat FROM tb_GiaBanSP WHERE MaSP = SP.ID)
GO

CREATE OR ALTER PROC up_SearchSanPham @StringSearch NVARCHAR(MAX)
AS
	SELECT ID, TenSP, TenNSP, GiaNiemYet, GiaBan, NgayCapNhat, SoLuongTon, ThoiGianBH, UrlHinh, TenTTSP FROM tb_SanPham SP, tb_TrangThaiSanPham TT, tb_GiaBanSP GB, tb_NhomSanPham NSP
	WHERE SP.TrangThai = TT.MaTTSP AND SP.ID = GB.MaSP AND SP.MaNSP = NSP.MaNSP
	AND NgayCapNhat >= ALL (SELECT NgayCapNhat FROM tb_GiaBanSP WHERE MaSP = SP.ID)
	AND (ID LIKE '%'+@StringSearch+'%' OR TenSP LIKE '%'+@StringSearch+'%' OR TenNSP LIKE '%'+@StringSearch+'%' OR GiaNiemYet LIKE '%'+@StringSearch+'%' OR GiaBan LIKE '%'+@StringSearch+'%' OR ThoiGianBH LIKE '%'+ @StringSearch +'%' OR TenTTSP LIKE '%'+@StringSearch+'%')
	AND SP.DaXoa = 0
GO

CREATE OR ALTER PROC up_GetCTHDNhap @MaHDNH INT
AS
	SELECT tb_CTHDNhapHang.ID, MaSP, TenSP, GiaNhap, SoLuong FROM tb_SanPham, tb_CTHDNhapHang
	WHERE tb_SanPham.ID = tb_CTHDNhapHang.MaSP
	AND MaHDNH = @MaHDNH
GO

CREATE OR ALTER PROC up_GetLichSuHDNH
AS
	SELECT MaHDNH, HoTen, NgayLap FROM tb_NhanVien, tb_HDNhapHang
	WHERE tb_NhanVien.MaNV = tb_HDNhapHang.MaNV
	AND MaHDNH IN (SELECT MaHDNH FROM tb_CTHDNhapHang GROUP BY MaHDNH)
GO

CREATE OR ALTER PROC up_GetLichSuHDNHByDate @From DATETIME, @To DATETIME
AS
	SELECT MaHDNH, HoTen, NgayLap FROM tb_NhanVien, tb_HDNhapHang
	WHERE tb_NhanVien.MaNV = tb_HDNhapHang.MaNV
	AND MaHDNH IN (SELECT MaHDNH FROM tb_CTHDNhapHang GROUP BY MaHDNH)
	AND NgayLap BETWEEN @From AND @To
GO

CREATE OR ALTER PROC up_GetNhanVien
AS
	SELECT NV.*, Password, CV.* FROM tb_NhanVien NV, tb_TaiKhoan TK, tb_ChucVu CV
	WHERE NV.Username = TK.Username AND TK.ChucVu = CV.MaChucVu
	AND DaXoa = 0
GO

CREATE OR ALTER PROC up_SearchNhanVien @StringSearch NVARCHAR(MAX)
AS
	SELECT NV.*, Password, CV.* FROM tb_NhanVien NV, tb_TaiKhoan TK, tb_ChucVu CV
	WHERE NV.Username = TK.Username AND TK.ChucVu = CV.MaChucVu
	AND (MaNV LIKE '%'+@StringSearch+'%' OR HoTen LIKE '%'+@StringSearch+'%' OR GioiTinh LIKE '%'+@StringSearch+'%' OR NgaySinh LIKE '%'+@StringSearch+'%' OR SDT LIKE '%'+@StringSearch+'%' OR NV.Username LIKE '%'+@StringSearch+'%' OR DiaChi LIKE '%'+@StringSearch+'%')
	AND DaXoa = 0
GO

CREATE OR ALTER PROC up_Login @Username VARCHAR(30), @Password VARCHAR(1000)
AS
	SELECT * FROM tb_TaiKhoan WHERE Username=@Username AND Password=@Password
GO

